    private void button_hinzufuegenActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_button_hinzufuegenActionPerformed

        if (newPersonNachname.getText().trim().isEmpty() || newPersonVorname.getText().trim().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Sie müssen einen Vor- und Nachnamen eingeben!", "Fehler", JOptionPane.WARNING_MESSAGE);
        } else {
            db.addName(newPersonNachname.getText().trim(), newPersonVorname.getText().trim());
            JOptionPane.showMessageDialog(this, "Die Person wurde hinzugefügt!", "Erfolg", JOptionPane.INFORMATION_MESSAGE);            
            daten_anzeigen();
        }
    }//GEN-LAST:event_button_hinzufuegenActionPerformed

    private void formComponentShown(java.awt.event.ComponentEvent evt) {//GEN-FIRST:event_formComponentShown

        daten_anzeigen();

    }//GEN-LAST:event_formComponentShown

    private void button_loeschenActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_button_loeschenActionPerformed

        if (ausgabe.getSelectedRow() >= 0) {
            db.delName(ausgabe.getValueAt(ausgabe.getSelectedRow(), 0).toString()); // 0 --> erste Spalte
            daten_anzeigen();
        } else {
            JOptionPane.showMessageDialog(this, "Sie müssen zuerst eine Person auswählen.", "Hinweis", JOptionPane.INFORMATION_MESSAGE);
        }
    }//GEN-LAST:event_button_loeschenActionPerformed

    private void button_bearbeitenActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_button_bearbeitenActionPerformed

        if (ausgabe.getSelectedRow() >= 0) {
            String person_alt = ausgabe.getValueAt(ausgabe.getSelectedRow(), 0).toString();
            GUI_PersonEdit frage = new GUI_PersonEdit(this, true, person_alt); // 0 --> erste Spalte
            frage.setVisible(true);
            String[] person_neu = frage.getReturnValue();
            if(person_neu != null){
                db.updateName(person_alt.split(", "), person_neu);
                daten_anzeigen();
            }
            
        } else {
            JOptionPane.showMessageDialog(this, "Sie müssen zuerst eine Person auswählen.", "Hinweis", JOptionPane.INFORMATION_MESSAGE);
        }
        
    }//GEN-LAST:event_button_bearbeitenActionPerformed


