    private void daten_anzeigen() {
        // Datenbankobjekt erstellen
        db = new DB_Anfragen();

        // Namen aus DB holen
        final ArrayList<String> namensliste = db.getNamensliste();

        // Namen anzeigen
        DefaultTableModel myListModel = new DefaultTableModel(new String[]{ausgabe.getModel().getColumnName(0)}, namensliste.size());

        for (int i = 0; i < namensliste.size(); i++) {
            myListModel.setValueAt(namensliste.get(i), i, 0);
        }

        ausgabe.setModel(myListModel);

        // DB-Verbindung schließen
        db.db_close();
    }
